package CountingValleys;

public class Main {

    public static void main(String[] args) {

        int steps = 1000;
        String path = "";
        int seaLvl = 0;
        int traverseCount = 0;

        for (int i=0; i < steps; i++){
            if(path.charAt(i) == 'U'){
                seaLvl++;
                if (seaLvl == 0) {
                    traverseCount++;
                }
            }else if(path.charAt(i) == 'D'){
                seaLvl--;
            }

        }


        System.out.println(traverseCount);
    }
}
